#!/bin/bash
# <bitbar.title>Coinbase.com Bitcoin Prices</bitbar.title>
# <bitbar.version>v1.0</bitbar.version>
# <bitbar.author>Charles Lehnert</bitbar.author>
# <bitbar.author.github>CLL80</bitbar.author.github>
# <bitbar.desc>It displays the current Bitcoin spot price in the menu bar as per Coinbase.com and omits the decimals for a compact view. Buy and sell prices can be seen by clicking.</bitbar.desc>
# <bitbar.image>https://raw.github.com/CLL80/bitbar-assets/master/screenshots/Cryptocurrency/Bitcoin/coinbase.com/spot-buy-and-sell.10s.png</bitbar.image>
bitcoin_icon=''

spot_price=$(curl -s "https://api.coinbase.com/v2/prices/BTC-USD/spot" -H "CB-VERSION: 2017-04-16" | egrep -o '"amount":"[0-9]+(\.)?' | sed 's/"amount"://'  | sed 's:^.\(.*\).$:\1:')
buy_price=$(curl -s "https://api.coinbase.com/v2/prices/BTC-USD/buy" -H "CB-VERSION: 2017-04-16" | egrep -o '"amount":"[0-9]+(\.)?' | sed 's/"amount"://'  | sed 's:^.\(.*\).$:\1:')
sell_price=$(curl -s "https://api.coinbase.com/v2/prices/BTC-USD/sell" -H "CB-VERSION: 2017-04-16" | egrep -o '"amount":"[0-9]+(\.)?' | sed 's/"amount"://'  | sed 's:^.\(.*\).$:\1:')

echo "$spot_price | templateImage=$bitcoin_icon"
echo "---"
echo "Spot price:  \$$spot_price"
echo "Buy price:    \$$buy_price"
echo "Sell price:    \$$sell_price"
