from urllib import urlopen
url = urlopen('https://coinmarketcap-nexuist.rhcloud.com/api/btc').read()

import json
result = json.loads(url)

def flow():
    if result ['change'] > '0':
        print (' £%.4f |  color=#000000'% float(result['price']['gbp']))
    else:
        print (' £%.4f |  color=#000000'% float(result['price']['gbp']))

flow()
