#!/bin/bash

# <xbar.title>Coinbase Prices</xbar.title>
# <xbar.version>v2.0</xbar.version>
# <xbar.author>Mustafa Türksavaş</xbar.author>
# <xbar.author.github>mustafaturksavas</xbar.author.github>
# <xbar.desc>Displays Coinbase exchange rates of USD for Bitcoin, Ethereum, Binance Coin, Dogecoin, XRP, Cardano, Litecoin, Neo, Monero, Dash.</xbar.desc>
# <xbar.image>https://media.giphy.com/media/3oEhmKvAlkJHoqalFK/giphy.gif</xbar.image>
# <xbar.dependencies>bash, python</xbar.dependencies>
#
# Based on Cryptocurrency Prices plugin by viiraj (github.com/viiraj).
#
# I hope this plugin provides value for you and if you feel like it, tips are always appreciated.
# Bitcoin: 19Nwwfn1bHviNXQ5st9eka6pVsWwpz7Kzz
# Ethereum: 0x8e8a7760cef2f3275b87d95ca4419a6fb1fc1300
# Litecoin: LVYrJcvdBmhazNoAtJhBvaSsyY2kWHwraJ

btc_icon=""
eth_icon=""
bnb_icon=""
doge_icon=""
xrp_icon=""
ada_icon=""
ltc_icon=""
xmr_icon=""
dash_icon=""

btc_price=$(curl "https://api.coinbase.com/v2/exchange-rates?currency=BTC" | python -c "import sys, json; print(json.load(sys.stdin)['data']['rates']['USD'])")
eth_price=$(curl "https://api.coinbase.com/v2/exchange-rates?currency=ETH" | python -c "import sys, json; print(json.load(sys.stdin)['data']['rates']['USD'])")
bnb_price=$(curl "https://api.coinbase.com/v2/exchange-rates?currency=BNB" | python -c "import sys, json; print(json.load(sys.stdin)['data']['rates']['USD'])")
doge_price=$(curl "https://api.coinbase.com/v2/exchange-rates?currency=DOGE" | python -c "import sys, json; print(json.load(sys.stdin)['data']['rates']['USD'])")
xrp_price=$(curl "https://api.coinbase.com/v2/exchange-rates?currency=XRP" | python -c "import sys, json; print(json.load(sys.stdin)['data']['rates']['USD'])")
ada_price=$(curl "https://api.coinbase.com/v2/exchange-rates?currency=ADA" | python -c "import sys, json; print(json.load(sys.stdin)['data']['rates']['USD'])")
ltc_price=$(curl "https://api.coinbase.com/v2/exchange-rates?currency=LTC" | python -c "import sys, json; print(json.load(sys.stdin)['data']['rates']['USD'])")
xmr_price=$(curl "https://api.coinbase.com/v2/exchange-rates?currency=XMR" | python -c "import sys, json; print(json.load(sys.stdin)['data']['rates']['USD'])")
dash_price=$(curl "https://api.coinbase.com/v2/exchange-rates?currency=DASH" | python -c "import sys, json; print(json.load(sys.stdin)['data']['rates']['USD'])")

echo "$btc_price | templateImage=$btc_icon"
echo "---"
echo "$eth_price | templateImage=$eth_icon"
echo "$bnb_price | templateImage=$bnb_icon"
echo "$doge_price | templateImage=$doge_icon"
echo "$xrp_price | templateImage=$xrp_icon"
echo "$ada_price | templateImage=$ada_icon"
echo "$ltc_price | templateImage=$ltc_icon"
echo "$neo_price | templateImage=$neo_icon"
echo "$xmr_price | templateImage=$xmr_icon"
echo "$dash_price | templateImage=$dash_icon"
