#!/bin/bash

# <bitbar.title>Crypto Currency Prices</bitbar.title>
# <bitbar.version>1.0.0</bitbar.version>
# <bitbar.author>Elan Trybuch</bitbar.author>
# <bitbar.author.github>elaniobro</bitbar.author.github>
# <bitbar.desc>Displays Crypto currencies buy prices of Bitcoin, Ethereum, Litecoin and Iota.</bitbar.desc>
# <bitbar.image></bitbar.image>
# <bitbar.dependencies>bash, python</bitbar.dependencies>
#
# Based on Cryptocurrency Prices plugin by viiraj (github.com/viiraj) &
# Based on Coinbase Prices plubing by mustafaturksavas (github.com/mustafaturksavas)
#
# I hope this plugin provides value for you and if you feel like it, tips are always appreciated.
# Bitcoin: 16GFRSLjgGoNCHgMepoq5pgmLzQKm9vgaW
# Ethereum: 0x74777ca1d8c5532902c020d7cEf050b01F42dd28
# Litecoin: LRWLD4ZNdQ6AtQ5VdYMxREkgYsESKwtDqd
# Iota: coming soon


bitcoin_icon=''
eth_icon=''
ltc_icon=''
iota_icon=''

bit_price=$(curl -s -H "CCP-Version: 2017-12-04" "https://api.coinmarketcap.com/v1/ticker/bitcoin/" | python -c "import sys, json; print json.load(sys.stdin)[0]['price_usd']")
eth_price=$(curl -s -H "CCP-Version: 2017-12-04" "https://api.coinmarketcap.com/v1/ticker/ethereum/" | python -c "import sys, json; print json.load(sys.stdin)[0]['price_usd']")
ltc_price=$(curl -s -H "CCP-Version: 2017-12-04" "https://api.coinmarketcap.com/v1/ticker/litecoin/" | python -c "import sys, json; print json.load(sys.stdin)[0]['price_usd']")
iota_price=$(curl -s -H "CCP-Version: 2017-12-04" "https://api.coinmarketcap.com/v1/ticker/iota/" | python -c "import sys, json; print(json.load(sys.stdin)[0]['price_usd'])")

echo "$bit_price | templateImage=$bitcoin_icon"
echo "$eth_price | templateImage=$eth_icon"
echo "$ltc_price | templateImage=$ltc_icon"
echo "$iota_price | templateImage=$iota_icon"