#!/bin/bash
# <bitbar.title>AnyConnect Umbrella Status</bitbar.title>
# <bitbar.version>v1.1</bitbar.version>
# <bitbar.author>Jason Masker</bitbar.author>
# <bitbar.author.github>jasmas</bitbar.author.github>
# <bitbar.desc>Check and toggle status of Umbrella Roaming Security Module for Cisco AnyConnect.</bitbar.desc>
# <bitbar.image>https://i.imgur.com/Jznovev.png</bitbar.image>

PLUGIN_BASE='/opt/cisco/anyconnect/bin/plugins'

IMG_DISABLED=''
IMG_ENABLED=''
IMG_TOGGLE=''

# Check plugin status, return 0 if enabled, 1 if disabled
function check_status {
    [[ -f $PLUGIN_BASE/libacumbrellaapi.dylib ]] &&
    [[ -f $PLUGIN_BASE/libacumbrellactrl.dylib ]] &&
    [[ -f $PLUGIN_BASE/libacumbrellaplugin.dylib ]]
}

# Check if plugin disabled by utility, return 0 if yes, 1 if no
function verify_plugin_disabled {
    [[ -f $PLUGIN_BASE/disabled/libacumbrellaapi.dylib ]] &&
    [[ -f $PLUGIN_BASE/disabled/libacumbrellactrl.dylib ]] &&
    [[ -f $PLUGIN_BASE/disabled/libacumbrellaplugin.dylib ]]
}

# Disable plugin
function disable_plugin {
    TMP=$(mktemp -d)
    pushd "$TMP" > /dev/null 2>&1
    /usr/bin/osacompile -e "do shell script \"mkdir -p $PLUGIN_BASE/disabled && mv -f $PLUGIN_BASE/libacumbrellaapi.dylib $PLUGIN_BASE/libacumbrellactrl.dylib $PLUGIN_BASE/libacumbrellaplugin.dylib $PLUGIN_BASE/disabled/\" with administrator privileges" -o 'Disable Cisco Umbrella.app'
    echo $IMG_TOGGLE | base64 -D > applet.png
    mkdir applet.iconset
    sips -z 32 32 applet.png --out applet.iconset/icon_32x32.png
    sips -z 64 64 applet.png --out applet.iconset/icon_32x32@2x.png
    iconutil -c icns applet.iconset
    mv applet.icns 'Disable Cisco Umbrella.app/Contents/Resources/'
    'Disable Cisco Umbrella.app/Contents/MacOS/applet'
    popd
    rm -rf "$TMP"
}

# Enable plugin
function enable_plugin {
    TMP=$(mktemp -d)
    pushd "$TMP" > /dev/null 2>&1
    /usr/bin/osacompile -e "do shell script \"mv -f $PLUGIN_BASE/disabled/libacumbrellaapi.dylib $PLUGIN_BASE/disabled/libacumbrellactrl.dylib $PLUGIN_BASE/disabled/libacumbrellaplugin.dylib $PLUGIN_BASE/\" with administrator privileges" -o 'Enable Cisco Umbrella.app'
    echo $IMG_TOGGLE | base64 -D > applet.png
    mkdir applet.iconset
    sips -z 32 32 applet.png --out applet.iconset/icon_32x32.png
    sips -z 64 64 applet.png --out applet.iconset/icon_32x32@2x.png
    iconutil -c icns applet.iconset
    mv applet.icns 'Enable Cisco Umbrella.app/Contents/Resources/'
    'Enable Cisco Umbrella.app/Contents/MacOS/applet'
    popd
    rm -rf "$TMP"
}

case "$1" in
    'enable')
        verify_plugin_disabled &&
        enable_plugin
        ;;
    'disable')
        check_status &&
        disable_plugin
        ;;
    *)
        check_status &&
        echo "| image=$IMG_ENABLED" ||
        echo "| image=$IMG_DISABLED"

        echo '---'

        (verify_plugin_disabled &&
        echo "Enable Cisco Umbrella| terminal=false refresh=true bash='$0' param1=enable") ||
        (check_status &&
        echo "Disable Cisco Umbrella| terminal=false refresh=true bash='$0' param1=disable")

esac
