# <bitbar.dependencies></bitbar.dependencies>

function openBrowser() {
  if [[ -d /Applications/Chromium.app ]]; then
    BROWSER="/Applications/Chromium.app"
  elif [[ -d /Applications/Google\ Chrome.app ]]; then
    BROWSER="/Applications/Google Chrome.app"
  fi
  open -n -a "${BROWSER}" --args --app="${1}"
}

function generateBitBar() {
  declare -a titles=(
    "Docs"         # Docs
    "Spreadsheets" # Spreadsheets
    "Slides"       # Slides
    "Notes"        # Notes
    "Contacts"     # Contacts
    "Calendars"    # Calendars
    "Pictures"     # Pictures
    "Mail"         # Mail
    "Translate"    # Translate
    "Drive"        # Drive
    "Maps"         # Maps
    "YouTube"      # YouTube
  )
  declare -a colors=(
    "#4889f1" # Docs
    "#2aa366" # Spreadsheets
    "#f4b830" # Slides
    "#edbc45" # Notes
    "#16319a" # Contacts
    "#5489ec" # Calendars
    "#9bc260" # Pictures
    "#f3f4f6" # Mail
    "#677b88" # Translate
    "#4c9c66" # Drive
    "#fade65" # Maps
    "#eb3323" # YouTube
  )
  declare -a urls=(
    "https://docs.google.com/document"     # Docs
    "https://docs.google.com/spreadsheets" # Spreadsheets
    "https://docs.google.com/presentation" # Slides
    "https://keep.google.com"              # Notes
    "https://contacts.google.com/"         # Contacts
    "https://calendar.google.com"          # Calendars
    "https://photos.google.com/"           # Pictures
    "https://mail.google.com/"             # Mail
    "https://translate.google.de/"         # Translate
    "https://drive.google.com/"            # Drive
    "https://maps.google.de/"               # Maps
    "https://www.youtube.com"              # YouTube
  )
  declare -a images=(
    # Docs
    ""
    # Spreadsheets
    ""
    # Slides
    ""
    # Notes
    ""
    # Contacts
    ""
    # Calendars
    ""
    # Pictures
    ""
    # Mail
    ""
    # Translate
    ""
    # Drive
    ""
    # Maps
    ""
    # YouTube
    ""
  )

  echo "| "
  echo "---"
  for (( i=0; i<${#titles[@]}; i++ )); do
    title="${titles[${i}]}"
    color="${colors[${i}]}"
    url="${urls[${i}]}"
    image="${images[${i}]}"
    echo "${title} | color=${color} bash='${0}' param1='${url}' terminal=false image=${image}"
  done
}

if [[ "${1}" != "" ]]; then
  openBrowser "${1}"
else
  generateBitBar
fi
